(1231, 34, '_single_blocks', 'field_59d2632da2cff'),
(1232, 134, 'lede_paragraph', 'Itâ€™s often said that the simplest ideas are most powerful, and this has certainly been the case for New Zealand photographer Stuart Robertson.'),
(1233, 134, '_lede_paragraph', 'field_59cbd393cd8f7'),
